import React, { useEffect, useState } from 'react'
import './css/Home.css'
import "react-responsive-carousel/lib/styles/carousel.min.css"; 
import { Carousel } from 'react-responsive-carousel'
import {Link} from "./react-router-dom";


function Home() {

    const [popularMovies, setPopularMovies]= useState([])

    useEffect (()=>{
    fetch("https://api.themoviedb.org/3/movie/popular?api_key=6194094c97fb5bd844f4e880a55e01f2&language=en-US")
    .then(res => res.json())
    .then(data => console.log(data.results))
    },[])

  return (
    <div className='poster'>
        <Carousel
        showThumbs={false}
        autoPlay={true}
        transitionTime={3}
        infiniteLoop={true}
        showStatus={false}
        >
            {
                popularMovies.map(movie => (
                    <Link style={{textDecoration:"none",color:"white"}} to={`/movie/${movie.id}`}>
                    <div className='posterImage'>
                        <img src={`https://image.tmbd.org/t/p/original${movie && movie.backdrop_path}`}/>
                    </div>
                    <div className='posterImage_overlay'>
                        <div className='posterImage_title'>{movie ? movie.original_title: ""}</div>
                        <span className='posterImage_rating'>
                            {movie ? movie.vote_average : " "}
                            <i className='fas fa-star'/>{" "}
                        </span>
                    </div>
                    <div className='posterimage_description'>{movie ? movie.overview : " "}</div>
                    
                    </Link>
                ))
            }
        </Carousel>
      
    </div>
  )
}

export default Home
